function AfficherPanier(){var t=JSON.parse(Cookies.get("cart")),e=0;t.forEach(t=>{let i=t.quantite>1?"s":"";elCartList.innerHTML+=`<li>${t.titre} - ${t.quantite} ticket${i} (${t.tarif}€)</li>`,e+=t.tarif*t.quantite}),elPrixTotal.innerText+=e.toString()+"€"}const elCartList=document.getElementById("cartList"),elPrixTotal=document.getElementById("prixTotal");null!=Cookies.get("cart")&&AfficherPanier();