function AfficherPanier(){var t=JSON.parse(Cookies.get("cart")),e=0;t.forEach(t=>{let i=t.quantite>1?"s":"",r=t.tarif*t.quantite;elCartList.innerHTML+=`<li>${t.titre} - ${t.quantite} ticket${i} (${r}€)</li>`,e+=r}),elPrixTotal.innerText+=e.toString()+"€"}const elCartList=document.getElementById("cartList"),elPrixTotal=document.getElementById("prixTotal");null!=Cookies.get("cart")&&AfficherPanier();